import useFetchData from '../customhook/userDataFetch';
import Loader from '../components/loader';
import CustomButton from '../components/customButton';
// import { getUsers } from '../services/userServices';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@mui/material';
const Home = () => {
    const { data, loading, error, refresh } = useFetchData('http://localhost:5000/product/GETALLPRODUCT');
    if (loading) return <Loader />;
    if (error) return <div>Error: {error}</div>;

    const handleDelete = async (id) => {
        try {
            await deleteUser(id);
            setProducts(products.filter(product => product._id !== id));
            console.log('Product deleted');
        } catch (error) {
            console.error('Failed to delete product:', error.message);
        }
    };

    // ✅ Handle Edit
    const handleEdit = (id) => {
        navigate(`/edit/${id}`); // Route to edit form
    };
    return (
        <>

            <TableContainer component={Paper}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Name</TableCell>
                            <TableCell>Description</TableCell>
                            <TableCell>Price</TableCell>
                            <TableCell>Stock</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {data?.data?.map(user => (
                            <TableRow key={user._id}>
                                <TableCell>{user.name}</TableCell>
                                <TableCell>{user.price}</TableCell>
                                <TableCell>{user.description}</TableCell>
                                <TableCell>{user.stock}</TableCell>
                                <TableCell>
                                    <CustomButton color="error" onClick={() =>
                                        handleDelete(user._id)
                                    }>Delete</CustomButton>
                                    <CustomButton color="error" onClick={() =>
                                        handleEdit(user._id)
                                    }>Delete</CustomButton>
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </>
    );
};

export default Home;
