import { useState } from 'react';
import { createUser } from '../services/userServices';
import CustomInput from '../components/customInput';
import CustomButton from '../components/customButton';
import { Dialog } from '@mui/material';

const AddUser = ({ isOpen }) => {
    const [formData, setFormData] = useState({ name: '', email: '' });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        await createUser(formData);
        setFormData({ name: '', price: '', description: "" });
    };
    return (
        <Dialog sx={"md"} open={isOpen}>
            <form onSubmit={handleSubmit}>
                <CustomInput name="name" value={formData.name} onChange={handleChange} label="Name" />
                <CustomInput name="price" value={formData.price} onChange={handleChange} label="Email" />
                <CustomInput name="description" value={formData.description} onChange={handleChange} label="Email" />
                <CustomInput name="stock" value={formData.email} onChange={handleChange} label="Email" />
                <CustomButton type="submit">Add User</CustomButton>
            </form>
        </Dialog>
    );
};

export default AddUser;
